      HoTenCha: data.HoTenCha || "hotencha", //string
      NamSinhCha: date("{dd}/{mm}/{yyyy}", inputCha.date), //string
      CMNDCha: data.CMNDCha || "cmndcha", //string

      HoTenNguoiGiamHo: data.HoTenNguoiGiamHo || "hotenNGH", //string
