      DienThoai: data.DienThoaiLienHe || "dienthoai", //string
      Email: data.MailLienHe || "email", //string

      IDKyThi: IDKyThi,
    };
    // console.log(DataPush);
    try {
      await fetch(
        "http://tuyensinh.huongvietedm.vn/api/TSAPIService/dangkytuyensinh",
        {
          method: "POST",
          mode: "no-cors",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify(DataPush),
        }
      )
        .then((response) => response.json())
        .then((responseJson) => {
          console.log(responseJson.Result);
          responseJson.Result.status
            ? showMessage({
                message: "Đăng ký thành công",
                description: `${responseJson.Result}`,
                duration: 3000,
                type: "success",
              })
            : showMessage({
                message: "Đăng ký thất bại",
                description: `${responseJson.Result}`,
                duration: 3000,
                type: "warning",
              });
        });
    } catch (e) {
      //
      showMessage({
        message: "Đăng ký thất bại",
        description: `${responseJson.Result}`,
        duration: 3000,
        type: "error",
      });
    }
  };
